'use strict';

/* eslint camelcase: "off" */
/* eslint max-len: "off" */

const assert = require('bsert');

const util = {
  log(...args) {
    console.log(...args);
  },

  write(str) {
    process.stdout.write(str);
  },

  flush() {
    ;
  },

  show_one_result(name, fails, nreps) {
    const istr = [' \u2717 ', ' \u2713 '];

    if (fails == null) {
      this.log('\x1b[92m%sPASS\x1b[0m: %s', istr[1], name);
    } else if (fails > 0) {
      this.log('\x1b[91m%sFAIL\x1b[0m: %s (%d/%d failed)',
               istr[0], name, fails, nreps);
    } else {
      this.log('\x1b[92m%sPASS\x1b[0m: %s', istr[1], name);
    }
  },

  show_test(name, just = 32) {
    this.write(`\x1b[38;5;33m${name}\x1b[0m: `);
    this.flush();
  },

  show_progress(failed) {
    if (failed)
      this.write('\x1b[91m.\x1b[0m');
    else
      this.write('\x1b[92m.\x1b[0m');

    this.flush();
  },

  show_timing(tname, tvals, just=32) {
    const mean = sum(tvals) / tvals.length;
    const samp_dev = total(tvals, mean);

    this.write(`\x1b[92m \u25f7 ${tname}\x1b[0m: `);

    this.log('%d ms, \u03c3=%d ms, max=%d ms, min=%d ms',
             mean, samp_dev, max(tvals), min(tvals));
  },

  show_timing_pair(tname, pvvals) {
    const [pvals, vvals] = pvvals;

    this.show_test(`Timings for ${tname}`, 0);
    this.write('\n');
    this.show_timing('Signing', pvals, 36);
    this.show_timing('Verifying', vvals, 36);
    this.write('\n');
    this.flush();
  },

  run_test(f, doc, nreps) {
    const ndisp = Math.max(1, nreps >>> 6);
    const parts = doc.split(',');
    const [test_name, fail_names] = [parts[0], parts.slice(1)];

    this.show_test(test_name);

    const fails = [];

    for (let i = 0; i < fail_names.length; i++)
      fails.push(0);

    let failed = false;

    for (let idx = 0; idx < nreps; idx++) {
      const checks = f();

      let cidx = 0;
      let c;

      for ([cidx, c] of checks.entries()) {
        if (!c) {
          failed = true;
          fails[cidx] += 1;
        }
      }

      assert(cidx + 1 === fails.length);

      if (idx % ndisp === ndisp - 1) {
        this.show_progress(failed);
        failed = false;
      }
    }

    this.write('\n');

    if (sum(fails) === 0) {
      this.show_one_result(`all ${test_name} subtests passed (${fails.length})`,
                           null, null);
    } else {
      for (const nf of fails)
        this.show_one_result(test_name, nf, nreps);
    }

    return [sum(fails.map(x => x > 0 ? 1 : 0)), fails.length];
  },

  run_all_tests(nreps, modname, tests) {
    this.show_test(`${modname} tests`, 0);
    this.write('\n');

    let fails = 0;
    let subtests = 0;

    for (const [test, doc] of tests) {
      const [f, s] = this.run_test(test, doc, nreps);
      fails += f;
      subtests += s;
    }

    this.show_test('Summary', 0);
    this.write('\n');

    if (fails === 0)
      this.show_one_result(`all ${subtests} subtests passed`, null, null);
    else
      this.show_one_result('some subtests did not pass', fails, subtests);

    this.write('\n');
  },

  primes_1024: null,
  primes_2048: null
};

