function sum(iter) {
  let ret = 0;
  for (const n of iter)
    ret += n;
  return ret;
}

function min(iter) {
  let ret = 0;

  for (const n of iter) {
    if (n < ret)
      ret = n;
  }

  return ret;
}

function max(iter) {
  let ret = 0;

  for (const n of iter) {
    if (n > ret)
      ret = n;
  }

  return ret;
}

function total(tvals, mean) {
  let sum = 0;

  for (const tval of tvals)
    sum += (tval - mean) ** 2;

  return Math.sqrt(sum / Math.max(1, tvals.length - 1));
}

